import axios from "axios";
import { createContext, useEffect, useReducer } from "react";
import { useActiveUserContext } from "./activeUserContext";

export const OrderContext = createContext();

export const orderReducer = (state, action) => {
  switch (action.type) {
    case "CREATE_ORDER":
      return {
       myorders: action.payload,
      };
    default:
      return state;
  }
};

export const OrderContextProvider = ({ children }) => {

    const initialOrders = JSON.parse(localStorage.getItem("myorders"))|| [];

    const { activeUser } = useActiveUserContext();
  
    const [state, dispatch] = useReducer(orderReducer, {
     myorders: initialOrders,
    });
  
// OrderContextProvider içinde
useEffect(() => {
    const savedOrders = JSON.parse(localStorage.getItem("myorders")) || [];
  
    // Eğer localStorage verileri durum verileriyle uyumlu değilse, güncelleme yapın
    if (JSON.stringify(savedOrders) !== JSON.stringify(state.myorders)) {
      dispatch({ type: "CREATE_ORDER", payload: savedOrders });
    }
  }, [state.myorders]);
  
  useEffect(() => {
    const fetchOrder = async () => {
      try {
        // Sepet bilgisini almak için HTTP isteği yapın
        const res = await axios.get(`/api/auth/user/${activeUser._id}/orderItems`);
        dispatch({ type: "CREATE_ORDER", payload: res.data.myorders });
      } catch (error) {
        console.error("Error fetching order items:", error);
      }
    };

    if (activeUser) {
      fetchOrder();
    }
  }, [activeUser]);

  const addOrder = (order) => {
    dispatch({ type: "CREATE_ORDER", payload: [...state.myorders, order] });
    // localStorage'ı güncelleyin
    localStorage.setItem("myorders", JSON.stringify([...state.myorders, order]));
  };
  

  return (
    <OrderContext.Provider value={{ ...state, dispatch, addOrder }}>
      {children}
    </OrderContext.Provider>
  );
};
