import axios from 'axios';
import React, { createContext, useState, useEffect } from 'react';
import { useActiveUserContext } from './activeUserContext';

export const OrderContext = createContext();

export const OrderProvider = ({ children }) => {
  const [myorders, setMyOrders] = useState(() => {
    const storedItems = localStorage.getItem('myorders');
    return storedItems ? JSON.parse(storedItems) : [];
  });

  const { activeUser } = useActiveUserContext();

  useEffect(() => {
    localStorage.setItem('myorders', JSON.stringify(myorders));
  }, [myorders]);

  const addOrder = (order) => {
    setMyOrders((prevOrders) => [...prevOrders, order]);
  };

  useEffect(() => {
    // Yeni bir fonksiyon oluşturup sunucudan siparişleri almak için kullanabilirsiniz
    const fetchOrdersFromServer = async () => {
      try {
        if (activeUser) {
          const response = await axios.get(
            `/api/auth/user/${activeUser._id}/orderItems`
          );
          setMyOrders(response.data.orderItems);
        }
      } catch (error) {
        console.error('Error fetching orders:', error);
      }
    };

    // Siparişleri sunucudan almak için fonksiyonu çağırın
    fetchOrdersFromServer();
  }, [activeUser]); // Kullanıcı oturum açtığında yapılacak isteği burada belirtin

  return (
    <OrderContext.Provider value={{ myorders, addOrder,storedItems }}>
      {children}
    </OrderContext.Provider>
  );
};
