import React, { createContext, useState, useEffect } from "react";
import { usePostsContext } from "../hooks/usePostsContext";
export const BasketContext = createContext();

export const BasketProvider = ({ children,post }) => {


  const [basketItems, setBasketItems] = useState(() => {
    const storedItems = localStorage.getItem("basketItems");
    return storedItems ? JSON.parse(storedItems) : [];
  });
  useEffect(() => {
    localStorage.setItem("basketItems", JSON.stringify(basketItems));
  }, [basketItems]);



  // const addToBasket = (post) => {
    
  //   setBasketItems([...basketItems, post]);
    
  // };


  const addToBasket=async()=>{
    const newItem={
      id: post?._id,
      title: post.title,
      description: post.description,
      size: post.size,
      price:post.price
    }
  }

  const removeFromBasket = (item) => {
    const updatedBasketItems = basketItems.filter(
      (basketItem) => basketItem._id !== item._id
    );
    setBasketItems(updatedBasketItems);
  };
 const total =()=>{
  let totalPrice = 0;

    basketItems.forEach((item) => {
      totalPrice += item.price;
    });

    return totalPrice;
 }
  return (
    <BasketContext.Provider
      value={{ basketItems, addToBasket, removeFromBasket,total }}
    >
      {children}
    </BasketContext.Provider>
  );
};
