import React, { useRef } from "react";
import emailjs from "@emailjs/browser";
import styled from "styled-components";
import "../../Pages/Contact/contact.css"
import Footer from "../../Components/Footer/Footer";
// npm i @emailjs/browser

const Contact = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_oi9tqsp",
        "template_kge70hm",
        form.current,
        "T6tZe2qbRUwXaqmPo"
      )
      .then(
        (result) => {
          console.log(result.text);
          console.log("message sent");
        },
        (error) => {
          console.log(error.text);
        }
      );
  };

  return (
<>
    <section class="map-section">
    <iframe
  className="map"
  src="https://www.google.com/maps/embed?pb=!1m14!1m12!1m3!1d24300.75708627206!2d49.8033069!3d40.41783005!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!5e0!3m2!1sen!2saz!4v1681770709020!5m2!1sen!2saz"
  width="990px"
  height="450"
  style={{ border: 0 }}
  allowFullScreen=""
  loading="lazy"
  referrerPolicy="no-referrer-when-downgrade"
></iframe>
       </section>
    <div className="footer-contact">

      
    <div className="contact-info">
      <div className="info1">
      <h4>California, United States</h4>
        <p>Santa Monica Boulevard</p>
      </div>
       <div className="info2">
       <h3>00 (440) 9865 562</h3>
        <p>Mon to Fri 9am to 6 pm</p>
       </div>
     
        <p>support@colorlib.com</p>
        <p>Send us your query anytime!</p>
      </div>

          <StyledContactForm>
      <form ref={form} onSubmit={sendEmail}>
        <label>Name</label>
        <input type="text" name="user_name" />
        <label>Email</label>
        <input type="email" name="user_email" />
        <label>Message</label>
        <textarea name="message" />
        <input type="submit" value="Send" />
      </form>
    </StyledContactForm>

    </div>

    <Footer/>
    </>
 
  );
};

export default Contact;

// Styles
const StyledContactForm = styled.div`
  width: 400px;


  form {
    display: flex;
    align-items: flex-start;
    flex-direction: column;
    width: 100%;
    font-size: 16px;

    input {
      width: 100%;
      height: 35px;
      padding: 7px;
      outline: none;
      border-radius: 5px;
      border: 1px solid rgb(220, 220, 220);

      &:focus {
        border: 2px solid rgba(0, 206, 158, 1);
      }
    }

    textarea {
      max-width: 100%;
      min-width: 100%;
      width: 100%;
      max-height: 100px;
      min-height: 100px;
      padding: 7px;
      outline: none;
      border-radius: 5px;
      border: 1px solid rgb(220, 220, 220);

      &:focus {
        border: 2px solid rgba(0, 206, 158, 1);
      }
    }

    label {
      margin-top: 1rem;
    }

    input[type="submit"] {
      margin-top: 2rem;
      cursor: pointer;
      background: rgb(249, 105, 14);
      color: white;
      border: none;
    }
  }
`;
