import  { createContext, useReducer } from "react";

export const BasketContext = createContext();

export const basketReducer = (state, action) =>{

    switch(action.type){
        // case "SET_BASKET":
        //     return{
        //         basketItems : action.payload
        //     }

        case "CREATE_BASKET":
            return{
                basketItems : action.payload
            }

        case "UPDATE_BASKET":
            return{
                basketItems : state.basketItems.map((item) => {

                    if(item._id === action.payload._id){
                        return action.payload;
                    }

                    return item;
                })
            }

        case "DELETE_BASKET":
            return{
                basketItems : state.basketItems.filter((item)=> item._id !== action.payload._id)
            }

        

        default : 
            return state
    }

}

export const BasketContextProvider = ({ children }) => {

  const initialBasketItems = JSON.parse(localStorage.getItem('basketItems')) || [];

  const [state, dispatch] = useReducer(basketReducer, {
    basketItems: initialBasketItems,})

    const addToBasket = (item) => {
      const updatedBasket = [...state.basketItems, item];
      localStorage.setItem('basketItems', JSON.stringify(updatedBasket));
      dispatch({ type: 'CREATE_BASKET', payload: updatedBasket });
    };
  
    const removeFromBasket = (item) => {
      const updatedBasket = state.basketItems.filter((basketItem) => basketItem._id !== item._id);
      localStorage.setItem('basketItems', JSON.stringify(updatedBasket));
      dispatch({ type: 'DELETE_BASKET', payload: updatedBasket });
    };

    const total =()=>{
      let totalPrice = 0;
    
        state.basketItems.forEach((item) => {
          totalPrice += item.price;
        });
    
        return totalPrice;
     }

    return (
        <BasketContext.Provider value={{...state, dispatch,addToBasket,removeFromBasket,total}}>
            { children }
        </BasketContext.Provider>
    )
}
