import React from "react";
import "./myorders.css";

function Myorders() {
  // Retrieve orders from localStorage
  const myorders = JSON.parse(localStorage.getItem("myorders"));

  return (
    <>
      <div className="bottom">

        <div className="blog_post_profile_wrapper">
          {myorders?.length > 0 ? (
            // Render myorders if there are items in the myorders array
            myorders.map((order, index) => (
              <div className="blog_profile_postt" key={index}>
                {order.items?.length > 0 ? (
                  // Display order details if there are items in the items array
                  <>
                    <img src={order.items?.postImage} alt="" />
                    <div className="mid__title">
                      <h3>{order.items?.title}</h3>
                      <p>Price: {order.items?.price}$</p>
                      <p>Address: {order.address}</p>
                    </div>
                  </>
                ) : (
                  // Render a message when there are no items in the items array
                  <p>No items found for this order.</p>
                )}
              </div>
            ))
          ) : (
            // Render a message when there are no orders
            <p>No orders found.</p>
          )}
        </div>
      </div>
    </>
  );
}

export default Myorders;
