import React from "react";
import "./myorders.css";

function Myorders() {
  // Retrieve orders from localStorage
  const orders = JSON.parse(localStorage.getItem("orders") || "[]");

  return (
    <>
      <div className="bottom">
        <div>
          <h4>All Products</h4>
        </div>

        <div className="blog_post_profile_wrapper">
          {orders.length > 0 ? (
            // Render orders if there are items in the orders array
            orders.map((order, index) => (
              <div className="blog_profile_post" key={index}>
                {order.items.length > 0 ? (
                  // Display order details if there are items in the items array
                  <>
                    <img src={order.items[0]?.postImage} alt="" />
                    <div className="mid__title">
                      <h3>{order.items[0]?.title}</h3>
                      <p>Price: {order.items[0]?.price}$</p>
                      <p>Address: {order.address}</p>
                    </div>
                  </>
                ) : (
                  // Render a message when there are no items in the items array
                  <p>No items found for this order.</p>
                )}
              </div>
            ))
          ) : (
            // Render a message when there are no orders
            <p>No orders found.</p>
          )}
        </div>
      </div>
    </>
  );
}

export default Myorders;
