import React, { useState } from 'react'
import { usePostsContext } from '../../hooks/usePostsContext';
import { useDispatch } from 'react-redux';
import { useActiveUserContext } from '../../context/activeUserContext';
import { useParams } from 'react-router-dom';

function Myorders() {
    const { id } = useParams();
    const { dispatch } = usePostsContext();
    const postDispatch = useDispatch();
    const { activeUser } = useActiveUserContext();
    const [postAuthor, setPostAuthor] = useState();
    const [profileImage, setProfileImage] = useState();
  
    const posts = localStorage.getItem("orders");
  return (
   <>
    <div className="bottom">
              <div>
                <h4>All Products</h4>
              </div>

              <div className="blog_post_profile_wrapper">
                {posts
                  ?.filter((post) => post?.authorId === id)
                  ?.map((post) => {
                    return (
                      <div className="blog_profile_post" key={post?._id}>
                        <img src={post?.postImage} alt="" />

                        <div className="mid__title">
                          <h3>{post?.title}</h3>
                        </div>

                        {activeUser?._id === postAuthor?._id ? (
                          <div className="bottom">
                          
                          </div>
                        ) : (
                          <div className="bottom">
                            <span>
                            
                            </span>
                          </div>
                        )}
                      </div>
                    );
                  })}
              </div>
            </div>
   </>
  )
}

export default Myorders
