import axios from "axios";
import  { createContext, useEffect, useReducer } from "react";
import { useActiveUserContext } from "./activeUserContext";

export const OrderContext = createContext();

export const orderReducer = (state, action) =>{

    switch(action.type){
        // case "SET_BASKET":
        //     return{
        //         basketItems : action.payload
        //     }

        case "CREATE_BASKET":
            return{
                orders : action.payload
            }

        // case "UPDATE_BASKET":
        //     return{
        //         orders : state.orders.map((item) => {

        //             if(item._id === action.payload._id){
        //                 return action.payload;
        //             }

        //             return item;
        //         })
        //     }

        // case "DELETE_BASKET":
        //     return{
        //         orders : state.orders.filter((item)=> item._id !== action.payload._id)
        //     }

        

        default : 
            return state
    }

}

export const OrderContextProvider = ({ children }) => {

  const initialorders = JSON.parse(localStorage.getItem("myorders")) || [];
  const { activeUser } = useActiveUserContext();


    const [state, dispatch] = useReducer(orderReducer, {
      orders: initialorders,    })

    useEffect(() => {
      localStorage.setItem("myorders", JSON.stringify(state.orders));
    }, [state.orders]);

    useEffect(() => {
      const fetchOrder = async () => {
        try {
          // Sepet bilgisini almak için HTTP isteği yapın
          const res = await axios.get(`/api/auth/user/${activeUser._id}/orderItems`);
          dispatch({ type: 'CREATE_ORDER', payload: res.data.orders });
        } catch (error) {
          console.error('Error fetching basket items:', error);
        }
      };
  
      if (activeUser) {
        fetchOrder();
      }
    }, [activeUser]);

    const addToOrder = (item) => {
      dispatch({ type: "CREATE_ORDER", payload: item });
    };
    

    // const removeFromBasket = (item) => {
    //   dispatch({ type: "DELETE_BASKET", payload: item });
    // };
    // const total =()=>{
    //   let totalPrice = 0;
    
    //     state.orders.forEach((item) => {
    //       totalPrice += item.price;
    //     });
    
    //     return totalPrice;
    //  }

    return (
        <OrderContext.Provider value={{...state, dispatch,addToOrder}}>
            { children }
        </OrderContext.Provider>
    )
}
