import React, { useEffect } from "react";
import "./myorders.css";
import axios from "axios";

function Myorders() {
  // Retrieve orders from localStorage
  const { activeUser } = useActiveUserContext();

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        if (activeUser) {
          // Kullanıcının siparişlerini almak için HTTP isteği yapın
          const res = await axios.get(`/api/auth/user/${activeUser._id}/orderItems`);
        }
      } catch (error) {
        console.error("Error fetching orders:", error);
      }
    };

    fetchOrders();
  }, [activeUser]);
  return (
    <>
      <div className="bottomm">
        <div className="blog_postt_profile_wrapper">
          {res?.length > 0 ? (
            // Render res if there are items in the res array
            res.map((order, index) => (
              <> 
                {order.items?.length > 0 ? (
                  // Display order details if there are items in the items array
                  order.items.map((item, itemIndex) => (
                    <div className="blog_profile_postt" key={itemIndex}>
                      <img src={item.postImage} alt="" />
                      <div className="mid__title">
                        <h3>{item.title}</h3>
                        <p>Price: {item.price}$</p>
                        <p>Address: {order.address}</p>
                      </div>
                    </div>
                  ))
                ) : (
                  // Render a message when there are no items in the items array
                  <p>No items found for this order.</p>
                )}
              </>
            ))
          ) : (
            // Render a message when there are no orders
            <p>No orders found.</p>
          )}
        </div>
      </div>
    </>
  );
}

export default Myorders;
