import { createContext, useReducer, useContext, useEffect } from "react";

export const CommentsContext = createContext();

export const commentsReducer = (state, action) => {
  switch (action.type) {
    case "SET_COMMENTS":
      return {
        comments: action.payload,
      };
    case "ADD_COMMENT":
      return {
        comments: [...state.comments, action.payload],
      };
    case "DELETE_COMMENT":
      return {
        comments: state.comments.filter(
          (comment) => comment._id !== action.payload._id
        ),
      };
    default:
      return state;
  }
};

export const useCommentsContext = () => useContext(CommentsContext);

export const CommentsContextProvider = ({ children }) => {
  const [state, dispatchComments] = useReducer(commentsReducer, {
    comments: [], // Initialize comments as an empty array
  });

  // Load comments from an external source (API, local storage, etc.) on initial render
  useEffect(() => {
    // Replace this with your API call to fetch comments or load from local storage
    const fetchComments = async () => {
      try {
        // Perform the API call or get comments from local storage
        // const res = await axios.get("/api/comments");
        // const commentsData = res.data.comments;

        // For demonstration, let's set comments to an empty array initially
        const commentsData = [];
        dispatchComments({ type: "SET_COMMENTS", payload: commentsData });
      } catch (error) {
        console.log(error.message);
      }
    };

    fetchComments();
  }, []);

  return (
    <CommentsContext.Provider value={{ ...state, dispatchComments }}>
      {children}
    </CommentsContext.Provider>
  );
};
