import axios from "axios";
import  { createContext, useEffect, useReducer } from "react";
import { useActiveUserContext } from "./activeUserContext";

export const OrderContext = createContext();

export const orderReducer = (state, action) =>{

    switch(action.type){


        case "CREATE_ORDER":
            return{
               myorders : action.payload
            }


        default : 
            return state
    }

}

export const OrderContextProvider = ({ children }) => {

    const initialorders = JSON.parse(localStorage.getItem("orders")) || [];
    const { activeUser } = useActiveUserContext();


    const [state, dispatch] = useReducer(orderReducer, {
        myorders: initialorders,
      });
    
      useEffect(() => {
        // Check if the initialorders fetched from localStorage is an array
        if (Array.isArray(initialorders)) {
          // If it's an array, update the state with the parsed value
          dispatch({ type: "CREATE_ORDER", payload: initialorders });
        } else {
          // If it's not an array or undefined, set an empty array in localStorage
          localStorage.setItem("orders", JSON.stringify([]));
        }
      }, []);

    useEffect(() => {
      const fetchOrder = async () => {
        try {
          // Sepet bilgisini almak için HTTP isteği yapın
          const res = await axios.get(`/api/auth/user/${activeUser._id}/orderItems`);
          dispatch({ type: 'CREATE_ORDER', payload: res.data.myorders });
        } catch (error) {
          console.error('Error fetching order items:', error);
        }
      };
  
      if (activeUser) {
        fetchOrder();
      }
    }, [activeUser]);

    const addOrder = (order) => {
        dispatch({ type: "CREATE_ORDER", payload: order });

      };

     

    return (
        <OrderContext.Provider value={{...state, dispatch,addOrder}}>
            { children }
        </OrderContext.Provider>
    )
}
